import React, {useState} from 'react'
import { Link, Navigate} from "react-router-dom";
import newRequest from '../utils/newRequest';

const Navbar = () => {

  const [open, setOpen] = useState(false);



  const currentUser = JSON.parse(localStorage.getItem("currentUser"))

  const handleLogout = async () => {
    try {
      await newRequest.post("/auth/logout")
      localStorage.setItem("currentUser", null)
      Navigate("/")
    } catch (err) {
      console.log(err)
    }
  }

  return (
    <div className='flex flex-col items-center bg-pink-400 text-white sticky top-0 z-10'>
    <div className='w-[1400px] flex items-center justify-between py-5 px-0'>
    <div className='font-bold text-lg'>
      <Link to='/'>
      <span>Global</span>
      <span className=''>Freelance</span>
      </Link>
    </div>
    <div className='flex items-center gap-6 font-medium'>
      <span className='whitespace-nowrap'>Explore Gigs</span>
      {!currentUser?.isSeller && <span>Become a Freelancer</span>}
      {currentUser ? (
            <div className="flex items-center gap-3 cursor-pointer relative" onClick={()=>setOpen(!open)}>
              <img
              className='w-8 h-8 rounded-full object-cover'
                src={currentUser.img || "../img/noavatar.jpg"}
                alt=""
              />
              <span>{currentUser?.username}</span>
              {open && <div className="absolute top-12 right-0 p-5 bg-white rounded-xl z-50 border  border-gray-300 flex flex-col gap-3 w-52 font-normal text-gray-500">
                {currentUser.isSeller && (
                  <>
                    <Link className="link" to="/mygigs">
                      Gigs
                    </Link>
                    <Link className="link" to="/add">
                      Add New Gig
                    </Link>
                  </>
                )}
                <Link className="link" to="/orders">
                  Orders
                </Link>
                <Link className="link" to="/messages">
                  Messages
                </Link>
                <Link className="link" onClick={handleLogout}>
                  Logout
                </Link>
              </div>}
            </div>
          ) : (
            <>
              <span>Sign in</span>
              <Link className="link" to="/register">
              <button className='py-1 px-5 border border-solid cursor-pointer bg-transparent  hover:bg-green-400'>Join</button>
              </Link>
            </>
          )}

    </div>
    </div>

    </div>
  )
}

export default Navbar
