import { Slider } from 'infinite-react-carousel/lib'
import React from 'react'
import { Link, useParams } from "react-router-dom";
import { useQuery } from "@tanstack/react-query";
import newRequest from "../utils/newRequest"
import Reviews from '../components/Reviews';

const Gig = () => {
  const { id } = useParams();

  const { isLoading, error, data } = useQuery({
    queryKey: ["gig"],
    queryFn: () =>
      newRequest.get(`/gigs/single/${id}`).then((res) => {
        return res.data;
      }),
  });

  const userId = data?.userId;

  const {
    isLoading: isLoadingUser,
    error: errorUser,
    data: dataUser,
  } = useQuery({
    queryKey: ["user"],
    queryFn: () =>
      newRequest.get(`/users/${userId}`).then((res) => {
        return res.data;
      }),
    enabled: !!userId,
  });

  return (
    <div className="flex justify-center">
      {isLoading ? (
        "loading"
      ) : error ? (
        "Something went wrong!"
      ) : (
        <div className="w-[1400px] px-0 py-8 flex gap-[50px]">
          <div className="flex flex-grow-2 flex-col gap-5">
            <span className="font-light uppercase text-sm text-[#555]">
              Fiverr {">"} Graphics & Design {">"}
            </span>
            <h1>{data.title}</h1>
            {isLoadingUser ? (
              "loading"
            ) : errorUser ? (
              "Something went wrong!"
            ) : (
              <div className="flex items-center gap-3">
                <img
                  className="w-8 h-8 rounded-[50%] object-cover"
                  src={dataUser.img || "/img/noavatar.jpg"}
                  alt=""
                />
                <span className='text-sm font-medium'>{dataUser.username}</span>
                {!isNaN(data.totalStars / data.starNumber) && (
                  <div className="flex items-center gap-1">
                    {Array(Math.round(data.totalStars / data.starNumber))
                      .fill()
                      .map((item, i) => (
                        <img src="/img/star.png" alt="" key={i} className="h-4 w-4" />
                      ))}
                    <span className='text-sm font-bold text-[#ffc108]'>{Math.round(data.totalStars / data.starNumber)}</span>
                  </div>
                )}
              </div>
            )}
            <Slider slidesToShow={1} arrowsScroll={1} className="bg-[#F5F5F5]">
              {data.images.map((img) => (
                <img key={img} src={img} alt="" className='max-h-[500px] object-cover' />
              ))}
            </Slider>
            <h2 className='font-normal'>About This Gig</h2>
            <p className='font-light leading-6 text-[#555]'>{data.desc}</p>
            {isLoadingUser ? (
              "loading"
            ) : errorUser ? (
              "Something went wrong!"
            ) : (
              <div className="mt-14 flex flex-col gap-5">
                <h2>About The Seller</h2>
                <div className="flex items-center gap-5">
                  <img className='w-[100px] h-[100px] rounded-[50%] object-cover' src={dataUser.img || "/img/noavatar.jpg"} alt=""  />
                  <div className="flex flex-col gap-3">
                    <span>{dataUser.username}</span>
                    {!isNaN(data.totalStars / data.starNumber) && (
                      <div className="flex items-center gap-2">
                        {Array(Math.round(data.totalStars / data.starNumber))
                          .fill()
                          .map((item, i) => (
                            <img className='h-4 w-4' src="/img/star.png" alt="" key={i} />
                          ))}
                        <span className='text-sm font-bold text-[#ffc108]'>
                          {Math.round(data.totalStars / data.starNumber)}
                        </span>
                      </div>
                    )}
                    <button className='bg-green-400 text-white rounded-md border border-solid p-3'>Contact Me</button>
                  </div>
                </div>
                <div className="border border-solid rounded-md p-5 mt-5">
                  <div className="flex justify-between flex-wrap">
                    <div className="w-[300px] flex flex-col gap-3 mb-5">
                      <span className="font-normal">From</span>
                      <span className="desc">{dataUser.country}</span>
                    </div>
                    <div className="w-[300px] flex flex-col gap-3 mb-5">
                      <span className="font-normal">Member since</span>
                      <span className="desc">Aug 2022</span>
                    </div>
                    <div className="w-[300px] flex flex-col gap-3 mb-5">
                      <span className="font-normal">Avg. response time</span>
                      <span className="desc">4 hours</span>
                    </div>
                    <div className="w-[300px] flex flex-col gap-3 mb-5">
                      <span className="font-normal">Last delivery</span>
                      <span className="desc">1 day</span>
                    </div>
                    <div className="w-[300px] flex flex-col gap-3 mb-5">
                      <span className="font-normal">Languages</span>
                      <span className="desc">English</span>
                    </div>
                  </div>
                  <hr />
                  <p>{dataUser.desc}</p>
                </div>
              </div>
            )}
            <Reviews gigId={id} />
          </div>
          <div className="flex flex-col flex-grow border border-solid p-6 rounded-md gap-5 max-h-[500px] sticky top-36">
            <div className="flex items-center justify-between">
              <h3 className='font-semibold'>{data.shortTitle}</h3>
              <h2 className='font-normal'>$ {data.price}</h2>
            </div>
            <p className='text-gray-600 my-3 mx-0'>{data.shortDesc}</p>
            <div className="flex items-center justify-between text-sm">
              <div className="flex items-center gap-3">
                <img className='w-5' src="/img/clock.png" alt="" />
                <span>{data.deliveryDate} Days Delivery</span>
              </div>
              <div className="flex items-center gap-3">
                <img className='w-5' src="/img/recycle.png" alt="" />
                <span>{data.revisionNumber} Revisions</span>
              </div>
            </div>
            <div className="features">
              {data.features.map((feature) => (
                <div className="flex items-center gap-3 font-light text-gray-600 mb-1" key={feature}>
                  <img className='w-4' src="/img/greencheck.png" alt="" />
                  <span>{feature}</span>
                </div>
              ))}
            </div>
            <Link to={`/pay/${id}`}>
            <button className='bg-[#1dbf73] p-3 text-white font-medium text-lg cursor-pointer'>Continue</button>
            </Link>
          </div>
        </div>
      )}
    </div>
  );
}


export default Gig
