import React, { useState } from 'react'
import { gigs } from '../data'
import GigCard from '../components/GigCard'

const Gigs = () => {
  const [sort, setSort] = useState('Sales')
 const [open, setOpen] = useState(false)

 const reSort = (type)=> {
   setSort(type)
   setOpen(false)
 }

  return (
    <div className='flex justify-center'>
      <div className="w-[1400px] py-7 px-0 flex flex-col gap-4">
        <span className='font-light text-sm text-[#555] uppercase'>GlobalFreelance > GRAPHICS & DESIGN </span>
        <h1>AI Artists</h1>
        <p className='text-[#999] font-light'>Explore the boundaries of art and technlogy with GlobalFreelance's AI Artists</p>
        <div className="flex items-center justify-between mb-5">
          <div className="flex items-center gap-3 text-[#555] font-light">
            <span>Budget</span>
            <input className='p-1 border border-solid rounded outline-none' type="text" placeholder='Min' />
            <input className='p-1 border border-solid rounded outline-none' type="text" placeholder='Max' />
            <button className='flex py-1 px-2 bg-[#1dbf73] text-white border-none font-medium rounded cursor-pointer'>Apply</button>
          </div>
          <div className="relative flex items-center gap-3">
            <span className='text-[#555] font-light'>SortBy</span>
            <div className="font-medium">{sort === "sales" ? "Best Selling" : "Newest"}</div>
            <img className='w-4 cursor-pointer' src="./img/down.png" alt="" onClick={() => setOpen(!open)} />
            {open && (
            <div className="p-5 bg-white border border-gray-200 rounded-md absolute top-7 right-0 z-10 flex flex-col gap-5 text-[#555]">
              {sort === "sales" ? (
              <span className='cursor-pointer' onClick={() => reSort("createdAt")}>Newest</span>
              ) : (
              <span className='cursor-pointer' onClick={() => reSort("sales")}>Best Selling</span>
              )}
            </div>
            )}
          </div>
        </div>
        <div className="flex flex-wrap justify-center">
        {gigs.map((gig) => (
            <GigCard key={gig.id} item={gig} />
          ))}
        </div>
      </div>
    </div>
  )
}

export default Gigs
